import React, {Component} from 'react'
import ReactDOM from 'react-dom';
import axios from 'axios';
import Header  from './Common/Header'
import Footer  from './Common/Footer'
import Sidebar  from './Common/Sidebar'
import baseUrl from '../CommonFunctions';



import logo from '.././assets/logo.png';




class Classes extends Component {
     constructor(){
        super()
     	this.state = {
     		userData:JSON.parse(localStorage.getItem("userData")),
		    data: [],
 		  }
     }

     handleGoEdit(url, e){
  	  this.props.history.push(url, { some: 'state' })
     }

componentDidMount() {
   console.log(this.state);
   axios.get(baseUrl('classes?local_association_id='+this.state.userData.local_association_id,'api') )
   .then(res => {
         if(res.data.errorCode=='200'){
          const data = res.data.data.map(obj => obj);
          this.setState({ data }); }
      });
  }

  updateStatus(status,id,e){
    axios.get( baseUrl('update-classes-status?id='+id+'&status='+status,'api') )
    .then(res => {
       if(res.data.errorCode=='200'){
         axios.get(baseUrl('classes?local_association_id='+this.state.userData.local_association_id,'api') )
         .then(res => {
           if(res.data.errorCode=='200'){
            const data = res.data.data.map(obj => obj);
            this.setState({ data }); }
        });
       }
    });
  }


 render(){
        return (
              <div>
              <Header/>
              <br/><br/>
              <div className="page-container">
              <Sidebar/>

		           <div className="page-content-wrapper">
					<div className="page-content">
					    <div className="portlet box green">
					                    <div className="portlet-title">
					                        <div className="caption">
					                            <i className="fa fa-cogs"></i>Classes</div>
                                                <button className="btn btn-success pull-right white"  onClick={this.handleBack} >Back</button>
                                                <button onClick={(e) => this.handleGoEdit('/new-classes' , e)} className="btn btn-success pull-right white"    >Add New</button>

					                    </div>
					                       <div className="portlet-body flip-scroll">

					                                {this.state.data.map(post =>
					                                       <div className="row quiz-row">
                                                              <div className="col-md-2"> <img className="thumb_img"  src={post.thumb_img} />   </div>
                                                               <div className="col-md-2"> {post.class_name} <br/>{post.from} to {post.to}  </div>
                                                               <div className="col-md-2"> {post.level_name}   </div>
                                                               <div className="col-md-2"> {post.position_name}   </div>

                                                               <div className="col-md-2"> <button    onClick={(e) => this.handleGoEdit('/update-classes/?id='+btoa(post.id) , e)}     className="btn btn-success btn-xs margin-r-5 "> Update </button> </div>
                                                               <div className="col-md-2 ">
                                                                             {post.is_delete=='0' &&( <button  onClick={(e) => this.updateStatus('1' ,post.id, e)} className="btn btn-xs btn-danger"> Deactivate</button>  )} 
                                                                             {post.is_delete=='1' &&( <button  onClick={(e) => this.updateStatus('0' ,post.id, e)} className="btn btn-xs btn-success"> Activate</button>  )}  
                                                              </div>
					                                       </div>
                                                    )}



					                    </div>
					                </div>
					     </div>
					</div>





             </div>
             <Footer/>
             </div>
        	)

 }

}

export default Classes
