import React, {Component} from 'react'
import ReactDOM from 'react-dom';
import axios from 'axios';

import Header  from './Common/Header'
import Footer  from './Common/Footer'
import Sidebar  from './Common/Sidebar'



import logo from '.././assets/logo.png';




class LocalAssociationsAdmin extends Component {
     constructor(){
        super()
     	this.state = {
     		userData:JSON.parse(localStorage.getItem("userData")),
		    data: [],
 		  }
     }

     handleGoEdit(url, e){
  	  this.props.history.push(url, { some: 'state' })
     }

componentDidMount() {
    this.setState({state_association_id:this.state.userData.state_association_id});

    axios.get('http://18.222.166.163:3000/local-associations-admins?state_association_id='+this.state.userData.state_association_id)
      .then(res => {

if(res.data.errorCode=='200'){
        const data = res.data.data.map(obj => obj);
         this.setState({ data }); }


      });
  }




 render(){
        return (
              <div>
              <Header/>
              <br/><br/>
              <div className="page-container">
              <Sidebar/>

		           <div className="page-content-wrapper">
					<div className="page-content">
					    <div className="portlet box green">
					                    <div className="portlet-title">
					                        <div className="caption">
					                            <i className="fa fa-cogs"></i>Local Associations</div>
                                                <button className="btn btn-success pull-right white"  onClick={this.handleBack} >Back</button>
                                                <button onClick={(e) => this.handleGoEdit('/new-local-associations-admin' , e)} className="btn btn-success pull-right white"    >Add New</button>
					                    </div>
					                       <div className="portlet-body flip-scroll">

                                           {this.state.data.map(post =>
					                                       <div className="row quiz-row">
                                                                <div className="col-md-3 "> {post.name} </div>
                                                                <div className="col-md-3 "> {post.email} </div>
                                                               <div className="col-md-3"> {post.state_association_name}   </div>

                                                               <div className="col-md-3"> <button    onClick={(e) => this.handleGoEdit('/update-state-associations-admin/?id='+btoa(post.id) , e)}     className="btn btn-success btn-xs margin-r-5 "> Update </button> </div>

					                                       </div>
					                                )}

					                    </div>
					                </div>
					     </div>
					</div>





             </div>
             <Footer/>
             </div>
        	)

 }

}

export default LocalAssociationsAdmin
