import React, {Component} from 'react'
import ReactDOM from 'react-dom';
import axios from 'axios';
import Header  from './Common/Header'
import Footer  from './Common/Footer'
import Sidebar  from './Common/Sidebar'
import baseUrl from '../CommonFunctions';



import logo from '.././assets/logo.png';




class Teachers extends Component {
     constructor(){
        super()
     	this.state = {
     		userData:JSON.parse(localStorage.getItem("userData")),
		    data: [],
 		  }
     }

     handleGoEdit(url, e){
  	  this.props.history.push(url, { some: 'state' })
     }

componentDidMount() {
   console.log(this.state);
    axios.get(baseUrl('teachers?local_association_id='+this.state.userData.local_association_id,'api') )
      .then(res => {
         if(res.data.errorCode=='200'){
          const data = res.data.data.map(obj => obj);
          this.setState({ data }); }
      });
  }

  updateStatus(status,id,e){
    axios.get( baseUrl('update-teacher-status?id='+id+'&status='+status,'api') )
    .then(res => {
       if(res.data.errorCode=='200'){
        axios.get(baseUrl('segments?local_association_id='+this.state.userData.local_association_id,'api') )
        .then(res => {
           if(res.data.errorCode=='200'){
            const data = res.data.data.map(obj => obj);
            this.setState({ data }); }
        });
       }
    });
  }


 render(){
        return (
              <div>
              <Header/>
              <br/><br/>
              <div className="page-container">
              <Sidebar/>

		           <div className="page-content-wrapper">
					<div className="page-content">
					    <div className="portlet box green">
					                    <div className="portlet-title">
					                        <div className="caption">
					                            <i className="fa fa-cogs"></i>Teachers</div>
                                                <button className="btn btn-success pull-right white"  onClick={this.handleBack} >Back</button>

					                    </div>
					                       <div className="portlet-body flip-scroll">

					                                {this.state.data.map(post =>
					                                       <div className="row quiz-row">
                                                              <div className="col-md-2"> <img className="thumb_img"  src={baseUrl(post.profile_pic,'profile_pic')  } />   </div>
                                                              <div className="col-md-2"> {post.name}   </div>
                                                               <div className="col-md-2"> {post.email}   </div>

                                                               <div className="col-md-2"> <button    onClick={(e) => this.handleGoEdit('/update-segments/?id='+btoa(post.id) , e)}     className="btn btn-success btn-xs margin-r-5 "> View Detail </button> </div>

					                                       </div>
                                                    )}

















					                    </div>
					                </div>
					     </div>
					</div>





             </div>
             <Footer/>
             </div>
        	)

 }

}

export default Teachers
